    // Method for set all arrays needed to rotate the bat;
    public void SetRotationArrays(float[] rotX, float[] rotY, float[] rotZ) {
        this._rotXFrames = rotX;
        this._rotYFrames = rotY;
        this._rotZFrames = rotZ;
    }

	public void SetPositionArrays(float[] posX, float[] posY, float[] posZ) {
    	this._posXFrames = posX; 
    	this._posYFrames = posY; 
    	this._posZFrames = posZ; 
    }

    // Create animation timeline;
    private Keyframe[] GenerateKeyFrames(float[] inputFramesArray)
    {
        Keyframe[] keys;
        keys = new Keyframe[inputFramesArray.Length];
        slider.minValue = 0;

        for (int i = 0; i < keys.Length; i++)
        {
            keys[i] = new Keyframe((float)i/100, inputFramesArray[i]);
            if(i == keys.Length - 1) {
                keys[i] = new Keyframe((float)i / 100, 0);
                slider.maxValue = (float)i / 100;
            }
        }
        return keys;
    }
